import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaEnvelope, FaMobile, FaIdCard, FaCamera, FaEye, FaEyeSlash } from 'lucide-react';
import * as faceapi from 'face-api.js';

const Login = () => {
    const navigate = useNavigate();
    const videoRef = useRef(null);
    const canvasRef = useRef(null);

    // Authentication states
    const [authStep, setAuthStep] = useState(1);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [mobile, setMobile] = useState('');
    const [otp, setOtp] = useState('');
    const [aadhaar, setAadhaar] = useState('');
    const [isOtpSent, setIsOtpSent] = useState(false);
    const [stream, setStream] = useState(null);
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    // Load face-api models
    useEffect(() => {
        const loadModels = async () => {
            try {
                await Promise.all([
                    faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
                    faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
                    faceapi.nets.faceRecognitionNet.loadFromUri('/models')
                ]);
            } catch (error) {
                console.error('Error loading face detection models:', error);
                setError('Failed to load face detection. Please try again.');
            }
        };
        loadModels();
    }, []);

    // Clean up video stream
    useEffect(() => {
        return () => {
            if (stream) {
                stream.getTracks().forEach(track => track.stop());
            }
        };
    }, [stream]);

    // Validation functions
    const validateEmail = (email) => {
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return regex.test(email);
    };

    const validatePassword = (password) => {
        return password.length >= 8;
    };

    const validateMobile = (mobile) => {
        const regex = /^[0-9]{10}$/;
        return regex.test(mobile);
    };

    const validateAadhaar = (aadhaar) => {
        const regex = /^[0-9]{12}$/;
        return regex.test(aadhaar);
    };

    // Authentication handlers
    const handleEmailPasswordSubmit = async () => {
        setError('');
        if (!validateEmail(email)) {
            setError('Please enter a valid email address');
            return;
        }
        if (!validatePassword(password)) {
            setError('Password must be at least 8 characters long');
            return;
        }

        setLoading(true);
        try {
            // Implement your email/password verification here
            // const response = await verifyEmailPassword(email, password);
            setAuthStep(2);
        } catch (error) {
            setError('Invalid email or password');
        } finally {
            setLoading(false);
        }
    };

    const handleSendOTP = async () => {
        setError('');
        if (!validateMobile(mobile)) {
            setError('Please enter a valid 10-digit mobile number');
            return;
        }

        setLoading(true);
        try {
            // Implement your OTP sending logic here
            // const response = await sendOTP(mobile);
            setIsOtpSent(true);
            // Simulated OTP response
            setTimeout(() => {
                setLoading(false);
            }, 1000);
        } catch (error) {
            setError('Failed to send OTP. Please try again.');
            setLoading(false);
        }
    };

    const handleVerifyOTP = async () => {
        setError('');
        if (!otp || otp.length !== 6) {
            setError('Please enter a valid 6-digit OTP');
            return;
        }

        setLoading(true);
        try {
            // Implement your OTP verification logic here
            // const response = await verifyOTP(mobile, otp);
            setAuthStep(3);
        } catch (error) {
            setError('Invalid OTP');
        } finally {
            setLoading(false);
        }
    };

    const handleAadhaarVerification = async () => {
        setError('');
        if (!validateAadhaar(aadhaar)) {
            setError('Please enter a valid 12-digit Aadhaar number');
            return;
        }

        setLoading(true);
        try {
            // Implement your Aadhaar verification logic here
            // const response = await verifyAadhaar(aadhaar);
            setAuthStep(4);
        } catch (error) {
            setError('Invalid Aadhaar number');
        } finally {
            setLoading(false);
        }
    };

    const startFacialRecognition = async () => {
        setError('');
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ video: true });
            setStream(stream);
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
            }
        } catch (error) {
            setError('Unable to access camera. Please check permissions.');
        }
    };

    const stopFacialRecognition = () => {
        if (stream) {
            stream.getTracks().forEach(track => track.stop());
            setStream(null);
        }
    };

    const handleFaceVerification = async () => {
        setError('');
        setLoading(true);
        try {
            const detections = await faceapi.detectAllFaces(
                videoRef.current,
                new faceapi.TinyFaceDetectorOptions()
            ).withFaceLandmarks();

            if (detections.length === 1) {
                stopFacialRecognition();
                // Navigate to dashboard after successful verification
                navigate('/dashboard');
            } else {
                setError('Face not detected. Please try again.');
            }
        } catch (error) {
            setError('Face verification failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg shadow-lg p-8 w-full max-w-md">
                <h1 className="text-2xl font-bold text-gray-800 text-center mb-8">
                    Secure Login
                </h1>

                {/* Progress Steps */}
                <div className="flex justify-between mb-8">
                    {[1, 2, 3, 4].map((step) => (
                        <div
                            key={step}
                            className={`w-8 h-8 rounded-full flex items-center justify-center ${authStep >= step
                                    ? 'bg-blue-600 text-white'
                                    : 'bg-gray-200 text-gray-600'
                                }`}
                        >
                            {step}
                        </div>
                    ))}
                </div>

                {/* Error Message */}
                {error && (
                    <div className="bg-red-50 text-red-500 p-3 rounded-lg mb-4">
                        {error}
                    </div>
                )}

                {/* Step 1: Email & Password */}
                {authStep === 1 && (
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Email Address
                            </label>
                            <div className="relative">
                                <FaEnvelope className="absolute left-3 top-3 text-gray-400" />
                                <input
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="pl-10 w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter your email"
                                />
                            </div>
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Password
                            </label>
                            <div className="relative">
                                <input
                                    type={showPassword ? "text" : "password"}
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    className="w-full p-2 border rounded-lg pr-10 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter your password"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    className="absolute right-3 top-2.5 text-gray-400"
                                >
                                    {showPassword ? <FaEyeSlash size={20} /> : <FaEye size={20} />}
                                </button>
                            </div>
                        </div>
                        <button
                            onClick={handleEmailPasswordSubmit}
                            disabled={loading}
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                        >
                            {loading ? 'Verifying...' : 'Continue'}
                        </button>
                    </div>
                )}

                {/* Step 2: Mobile OTP */}
                {authStep === 2 && (
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Mobile Number
                            </label>
                            <div className="relative">
                                <FaMobile className="absolute left-3 top-3 text-gray-400" />
                                <input
                                    type="tel"
                                    value={mobile}
                                    onChange={(e) => setMobile(e.target.value)}
                                    className="pl-10 w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter mobile number"
                                />
                            </div>
                        </div>
                        {isOtpSent && (
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    OTP
                                </label>
                                <input
                                    type="text"
                                    value={otp}
                                    onChange={(e) => setOtp(e.target.value)}
                                    className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter OTP"
                                    maxLength={6}
                                />
                            </div>
                        )}
                        <button
                            onClick={isOtpSent ? handleVerifyOTP : handleSendOTP}
                            disabled={loading}
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                        >
                            {loading
                                ? 'Processing...'
                                : isOtpSent
                                    ? 'Verify OTP'
                                    : 'Send OTP'}
                        </button>
                    </div>
                )}

                {/* Step 3: Aadhaar Verification */}
                {authStep === 3 && (
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Aadhaar Number
                            </label>
                            <div className="relative">
                                <FaIdCard className="absolute left-3 top-3 text-gray-400" />
                                <input
                                    type="text"
                                    value={aadhaar}
                                    onChange={(e) => setAadhaar(e.target.value)}
                                    className="pl-10 w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter Aadhaar number"
                                    maxLength={12}
                                />
                            </div>
                        </div>
                        <button
                            onClick={handleAadhaarVerification}
                            disabled={loading}
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                        >
                            {loading ? 'Verifying...' : 'Verify Aadhaar'}
                        </button>
                    </div>
                )}

                {/* Step 4: Face Recognition */}
                {authStep === 4 && (
                    <div className="space-y-4">
                        <div className="relative w-full h-64 bg-gray-100 rounded-lg overflow-hidden">
                            <video
                                ref={videoRef}
                                className="absolute w-full h-full object-cover"
                                autoPlay
                                muted
                            />
                            <canvas
                                ref={canvasRef}
                                className="absolute w-full h-full"
                            />
                        </div>
                        {!stream ? (
                            <button
                                onClick={startFacialRecognition}
                                className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                            >
                                <FaCamera className="inline mr-2" />
                                Start Face Verification
                            </button>
                        ) : (
                            <button
                                onClick={handleFaceVerification}
                                disabled={loading}
                                className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                            >
                                {loading ? 'Verifying...' : 'Verify Face'}
                            </button>
                        )}
                    </div>
                )}

                {/* Back Button */}
                {authStep > 1 && (
                    <button
                        onClick={() => setAuthStep(authStep - 1)}
                        className="mt-4 w-full bg-gray-100 text-gray-700 p-2 rounded-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
                    >
                        Back
                    </button>
                )}
            </div>
        </div>
    );
};

export default Login;
