import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Webcam from 'react-webcam';
import * as faceapi from 'face-api.js';
import "./Auth.css";

const Signup = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    // State for form data and face detection
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        password: "",
    });
    const [errors, setErrors] = useState({});
    const [authStep, setAuthStep] = useState('credentials');
    const [modelsLoaded, setModelsLoaded] = useState(false);
    const [loading, setLoading] = useState(false);
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceRegistered, setFaceRegistered] = useState(false);

    // Load face detection models
    const loadModels = async () => {
        const MODEL_URL = process.env.PUBLIC_URL + '/models';
        try {
            await Promise.all([
                faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
                faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
                faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
            ]);
            return true;
        } catch (error) {
            console.error('Model loading failed:', error);
            return false;
        }
    };

    useEffect(() => {
        (async () => {
            setLoading(true);
            const success = await loadModels();
            setModelsLoaded(success);
            setLoading(false);
        })();

        return () => {
            if (webcamRef.current?.video) {
                const stream = webcamRef.current.video.srcObject;
                stream?.getTracks().forEach(track => track.stop());
            }
        };
    }, []);

    // Capture and store face descriptor
    const captureAndStoreFace = async () => {
        if (!webcamRef.current) return false;

        try {
            const detections = await faceapi.detectSingleFace(
                webcamRef.current.video,
                new faceapi.TinyFaceDetectorOptions()
            ).withFaceLandmarks().withFaceDescriptor();

            if (detections) {
                localStorage.setItem('faceDescriptor', JSON.stringify(Array.from(detections.descriptor)));
                return true;
            }
        } catch (error) {
            console.error('Face capture error:', error);
        }
        return false;
    };

    // Handle input changes and validations (previous implementation)
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const validate = () => {
        const newErrors = {};
        if (!formData.name.trim()) newErrors.name = "Name is required.";
        if (!formData.email.trim()) {
            newErrors.email = "Email is required.";
        } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
            newErrors.email = "Enter a valid email address.";
        }
        if (!formData.password.trim() || formData.password.length < 6) {
            newErrors.password = "Password must be at least 6 characters.";
        }
        return newErrors;
    };

    // Handle credentials submission
    const handleCredentialsSubmit = (e) => {
        e.preventDefault();
        const newErrors = validate();
        if (Object.keys(newErrors).length > 0) {
            setErrors(newErrors);
        } else {
            setAuthStep('face');
        }
    };

    // Handle face registration
    const handleFaceRegistration = async () => {
        if (!webcamRef.current?.video || !modelsLoaded) return;

        try {
            const registered = await captureAndStoreFace();
            if (registered) {
                setFaceDetected(true);
                setFaceRegistered(true);

                // Store user details in localStorage or send to backend
                localStorage.setItem('userDetails', JSON.stringify(formData));
                localStorage.setItem('token', 'mock-token'); // Replace with actual token
                localStorage.setItem('userRole', 'user');

                // Navigate to dashboard after a short delay
                setTimeout(() => navigate('/dashboard'), 1500);
            }
        } catch (error) {
            console.error('Face registration error:', error);
        }
    };

    // Render different auth steps
    const renderCredentialsStep = () => (
        <form onSubmit={handleCredentialsSubmit}>
            <div className="form-group">
                <label htmlFor="name">Name</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    placeholder="Enter your name"
                    value={formData.name}
                    onChange={handleInputChange}
                />
                {errors.name && <small className="error-text">{errors.name}</small>}
            </div>
            <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    placeholder="Enter your email"
                    value={formData.email}
                    onChange={handleInputChange}
                />
                {errors.email && <small className="error-text">{errors.email}</small>}
            </div>
            <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                    type="password"
                    id="password"
                    name="password"
                    placeholder="Enter your password"
                    value={formData.password}
                    onChange={handleInputChange}
                />
                {errors.password && <small className="error-text">{errors.password}</small>}
            </div>
            <button className="auth-button" type="submit">
                Next
            </button>
        </form>
    );

    const renderFaceRegistrationStep = () => (
        <div className="face-registration-container">
            <h3>Register Your Face</h3>
            <div className="webcam-container">
                <Webcam
                    ref={webcamRef}
                    screenshotFormat="image/jpeg"
                    className="webcam"
                    onUserMedia={handleFaceRegistration}
                />
                {faceDetected && (
                    <div className={`face-status ${faceRegistered ? 'registered' : ''}`}>
                        {faceRegistered ? 'Face Registered ✓' : 'Detecting Face...'}
                    </div>
                )}
            </div>
            <button
                className="auth-button secondary"
                onClick={() => setAuthStep('credentials')}
            >
                Back
            </button>
        </div>
    );

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Create an Account</h2>
                {authStep === 'credentials'
                    ? renderCredentialsStep()
                    : renderFaceRegistrationStep()}

                {authStep === 'credentials' && (
                    <p className="auth-footer">
                        Already have an account?{" "}
                        <button
                            className="link-button"
                            onClick={() => navigate("/")}
                        >
                            Log In
                        </button>
                    </p>
                )}
            </div>
        </div>
    );
};

export default Signup;
