import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import * as faceapi from 'face-api.js';

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    const [modelsLoaded, setModelsLoaded] = useState(false);
    const [authStep, setAuthStep] = useState('credentials');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        aadharNumber: ''
    });

    // Load face-api models
    useEffect(() => {
        const loadModels = async () => {
            try {
                setLoading(true);
                // Make sure models are in the public folder under 'models'
                const MODEL_URL = '/models';

                await Promise.all([
                    faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL),
                    faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL),
                    faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL)
                ]);

                setModelsLoaded(true);
            } catch (error) {
                setError('Failed to initialize face detection');
                console.error('Error loading models:', error);
            } finally {
                setLoading(false);
            }
        };

        loadModels();
    }, []);

    // Face detection logic
    const handleFaceDetection = async () => {
        if (!webcamRef.current || !modelsLoaded) return;

        const interval = setInterval(async () => {
            try {
                if (webcamRef.current && webcamRef.current.video) {
                    const detection = await faceapi
                        .detectSingleFace(
                            webcamRef.current.video,
                            new faceapi.TinyFaceDetectorOptions()
                        )
                        .withFaceLandmarks();

                    if (detection) {
                        setFaceDetected(true);
                        clearInterval(interval);
                        setTimeout(() => {
                            setFaceMatched(true);
                            setTimeout(() => navigate('/dashboard'), 1000);
                        }, 1500);
                    }
                }
            } catch (error) {
                console.error('Face detection error:', error);
            }
        }, 100);

        return () => clearInterval(interval);
    };

    // Rest of the component remains the same...
    // (Keep all the existing input handling, validation, and rendering code)

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Secure Login</h2>
                {error && <div className="error-message">{error}</div>}

                {/* Rest of your JSX remains the same... */}

                {authStep === 'face' && (
                    <div className="face-auth-container">
                        {!modelsLoaded ? (
                            <div className="loading-message">
                                Initializing face detection...
                            </div>
                        ) : (
                            <div className="webcam-container">
                                <Webcam
                                    ref={webcamRef}
                                    screenshotFormat="image/jpeg"
                                    className="webcam"
                                    onUserMedia={handleFaceDetection}
                                />
                                {faceDetected && (
                                    <div className="face-detected">
                                        {faceMatched ? 'Face Verified ✓' : 'Detecting Face...'}
                                    </div>
                                )}
                            </div>
                        )}
                        {faceMatched && (
                            <div className="success-message">
                                Authentication successful! Redirecting...
                            </div>
                        )}
                    </div>
                )}

                {/* Rest of your JSX remains the same... */}
            </div>
        </div>
    );
};

export default Login;
