import React, { useState, useEffect } from "react";
import PropTypes from 'prop-types';
import { FaRegCalendarAlt, FaRegFlag, FaRegCommentDots } from "react-icons/fa";
import "./Dashboard.css";

// Constants for status colors
const STATUS_COLORS = {
    Active: "#4CAF50",    // Green
    Closed: "#F44336",    // Red
    Upcoming: "#2196F3",  // Blue
    Default: "#9E9E9E"    // Gray
};

const Dashboard = () => {
    const [elections, setElections] = useState([
        { id: 1, name: "Student Council Election", status: "Active" },
        { id: 2, name: "Company Representative Election", status: "Closed" },
    ]);

    const [news, setNews] = useState([]);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState("");

    const NEWS_API_KEY = "8280846cdd1a49ef880809767d0e61c8";

    useEffect(() => {
        const fetchElectionNews = async () => {
            try {
                const response = await fetch(
                    `https://newsapi.org/v2/everything?q=elections&apiKey=${NEWS_API_KEY}`
                );
                const data = await response.json();

                const articles = data.articles.slice(0, 3).map((article, index) => ({
                    id: index + 1,
                    title: article.title,
                    description: article.description,
                    imageUrl: article.urlToImage || "https://via.placeholder.com/150",
                }));

                setNews(articles);
            } catch (error) {
                console.error("Error fetching news:", error);
            }
        };

        fetchElectionNews();
    }, []);

    const addElection = () => {
        const newElection = {
            id: elections.length + 1,
            name: `Election ${elections.length + 1}`,
            status: "Upcoming"
        };

        setElections(prevElections => [...prevElections, newElection]);
    };

    const handleAddComment = () => {
        if (!newComment.trim()) return;

        const commentToAdd = {
            id: comments.length + 1,
            text: newComment.trim()
        };

        setComments(prevComments => [...prevComments, commentToAdd]);
        setNewComment(""); // Reset input field
    };

    const getStatusColor = (status) => {
        return STATUS_COLORS[status] || STATUS_COLORS.Default;
    };

    return (
        <div className="dashboard-container">
            <header>
                <h1>Decentralized Voting System Dashboard</h1>
            </header>

            {/* Election List Section */}
            <section className="election-section">
                <div className="section-header">
                    <h2>Available Elections</h2>
                    <button
                        className="auth-button add-election-btn"
                        onClick={addElection}
                    >
                        Add Election
                    </button>
                </div>

                <div className="election-cards">
                    {elections.map((election) => (
                        <div
                            key={election.id}
                            className="election-card"
                            style={{
                                borderColor: getStatusColor(election.status),
                                borderWidth: '3px',
                                borderStyle: 'solid'
                            }}
                        >
                            <div className="election-card-content">
                                <h3>{election.name}</h3>
                                <p
                                    className="status"
                                    style={{ color: getStatusColor(election.status) }}
                                >
                                    {election.status}
                                </p>
                                <FaRegCalendarAlt
                                    className="icon"
                                    color={getStatusColor(election.status)}
                                />
                            </div>
                        </div>
                    ))}
                </div>
            </section>

            {/* News Section */}
            <section className="news-section">
                <h2>Latest Election News</h2>
                <div className="news-cards">
                    {news.length > 0 ? (
                        news.map((item) => (
                            <div className="news-card" key={item.id}>
                                <img
                                    src={item.imageUrl}
                                    alt={`News about ${item.title}`}
                                    className="news-image"
                                />
                                <div className="news-content">
                                    <h3>{item.title}</h3>
                                    <p>{item.description}</p>
                                    <FaRegFlag className="icon" />
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>Loading election news...</p>
                    )}
                </div>
            </section>

            {/* Voter Opinions Section */}
            <section className="comments-section">
                <h2>Voter Opinions</h2>
                <div className="comments-display">
                    {comments.map((comment) => (
                        <div className="comment-card" key={comment.id}>
                            <p>{comment.text}</p>
                            <FaRegCommentDots className="icon" />
                        </div>
                    ))}
                </div>
                <div className="add-comment">
                    <input
                        type="text"
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="Share your opinion..."
                        className="comment-input"
                        onKeyPress={(e) => e.key === 'Enter' && handleAddComment()}
                    />
                    <button
                        onClick={handleAddComment}
                        className="auth-button"
                        disabled={!newComment.trim()}
                    >
                        Add Opinion
                    </button>
                </div>
            </section>
        </div>
    );
};

// PropTypes for type checking (optional but recommended)
Dashboard.propTypes = {
    // Add prop type validations if you pass any props in the future
};

export default Dashboard;
