import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Mail, Smartphone, CreditCard, Camera, Eye, EyeOff } from 'lucide-react';

const Login = () => {
    const navigate = useNavigate();
    const videoRef = useRef(null);
    const [stream, setStream] = useState(null);

    // Authentication states
    const [authStep, setAuthStep] = useState(1);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [mobile, setMobile] = useState('');
    const [otp, setOtp] = useState('');
    const [aadhaar, setAadhaar] = useState('');
    const [isOtpSent, setIsOtpSent] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [capturedImage, setCapturedImage] = useState(null);

    // Validation functions
    const validateEmail = (email) => {
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return regex.test(email);
    };

    const validatePassword = (password) => {
        return password.length >= 8;
    };

    const validateMobile = (mobile) => {
        const regex = /^[0-9]{10}$/;
        return regex.test(mobile);
    };

    const validateAadhaar = (aadhaar) => {
        const regex = /^[0-9]{12}$/;
        return regex.test(aadhaar);
    };

    // Authentication handlers
    const handleEmailPasswordSubmit = async () => {
        setError('');
        if (!validateEmail(email)) {
            setError('Please enter a valid email address');
            return;
        }
        if (!validatePassword(password)) {
            setError('Password must be at least 8 characters long');
            return;
        }

        setLoading(true);
        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep(2);
        } catch (error) {
            setError('Invalid email or password');
        } finally {
            setLoading(false);
        }
    };

    const handleSendOTP = async () => {
        setError('');
        if (!validateMobile(mobile)) {
            setError('Please enter a valid 10-digit mobile number');
            return;
        }

        setLoading(true);
        try {
            // Simulate OTP sending
            await new Promise(resolve => setTimeout(resolve, 1000));
            setIsOtpSent(true);
        } catch (error) {
            setError('Failed to send OTP. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleVerifyOTP = async () => {
        setError('');
        if (!otp || otp.length !== 6) {
            setError('Please enter a valid 6-digit OTP');
            return;
        }

        setLoading(true);
        try {
            // Simulate OTP verification
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep(3);
        } catch (error) {
            setError('Invalid OTP');
        } finally {
            setLoading(false);
        }
    };

    const handleAadhaarVerification = async () => {
        setError('');
        if (!validateAadhaar(aadhaar)) {
            setError('Please enter a valid 12-digit Aadhaar number');
            return;
        }

        setLoading(true);
        try {
            // Simulate Aadhaar verification
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep(4);
        } catch (error) {
            setError('Invalid Aadhaar number');
        } finally {
            setLoading(false);
        }
    };

    const startCamera = async () => {
        setError('');
        try {
            const mediaStream = await navigator.mediaDevices.getUserMedia({
                video: {
                    facingMode: "user",
                    width: { ideal: 640 },
                    height: { ideal: 480 }
                }
            });
            setStream(mediaStream);
            if (videoRef.current) {
                videoRef.current.srcObject = mediaStream;
            }
        } catch (error) {
            setError('Unable to access camera. Please check permissions.');
        }
    };

    const stopCamera = () => {
        if (stream) {
            stream.getTracks().forEach(track => track.stop());
            setStream(null);
        }
    };

    const captureImage = () => {
        if (!videoRef.current) return;

        const canvas = document.createElement('canvas');
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(videoRef.current, 0, 0);

        const imageData = canvas.toDataURL('image/jpeg');
        setCapturedImage(imageData);
        stopCamera();
    };

    const handleFaceVerification = async () => {
        if (!capturedImage) {
            setError('Please capture your image first');
            return;
        }

        setLoading(true);
        try {
            // Simulate face verification
            await new Promise(resolve => setTimeout(resolve, 1500));
            navigate('/dashboard');
        } catch (error) {
            setError('Face verification failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    // Clean up on unmount
    React.useEffect(() => {
        return () => {
            stopCamera();
        };
    }, []);

    return (
        <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg shadow-lg p-8 w-full max-w-md">
                <h1 className="text-2xl font-bold text-gray-800 text-center mb-8">
                    Secure Login
                </h1>

                {/* Progress Steps */}
                <div className="flex justify-between mb-8">
                    {[1, 2, 3, 4].map((step) => (
                        <div
                            key={step}
                            className={`w-8 h-8 rounded-full flex items-center justify-center ${authStep >= step
                                    ? 'bg-blue-600 text-white'
                                    : 'bg-gray-200 text-gray-600'
                                }`}
                        >
                            {step}
                        </div>
                    ))}
                </div>

                {/* Error Message */}
                {error && (
                    <div className="bg-red-50 text-red-500 p-3 rounded-lg mb-4">
                        {error}
                    </div>
                )}

                {/* Step 1: Email & Password */}
                {authStep === 1 && (
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Email Address
                            </label>
                            <div className="relative">
                                <Mail className="absolute left-3 top-3 text-gray-400 h-5 w-5" />
                                <input
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="pl-10 w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter your email"
                                />
                            </div>
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Password
                            </label>
                            <div className="relative">
                                <input
                                    type={showPassword ? "text" : "password"}
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    className="w-full p-2 border rounded-lg pr-10 focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter your password"
                                />
                                <button
                                    type="button"
                                    onClick={() => setShowPassword(!showPassword)}
                                    className="absolute right-3 top-2.5 text-gray-400"
                                >
                                    {showPassword ?
                                        <EyeOff className="h-5 w-5" /> :
                                        <Eye className="h-5 w-5" />
                                    }
                                </button>
                            </div>
                        </div>
                        <button
                            onClick={handleEmailPasswordSubmit}
                            disabled={loading}
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                        >
                            {loading ? 'Verifying...' : 'Continue'}
                        </button>
                    </div>
                )}

                {/* Step 2: Mobile OTP */}
                {authStep === 2 && (
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Mobile Number
                            </label>
                            <div className="relative">
                                <Smartphone className="absolute left-3 top-3 text-gray-400 h-5 w-5" />
                                <input
                                    type="tel"
                                    value={mobile}
                                    onChange={(e) => setMobile(e.target.value)}
                                    className="pl-10 w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter mobile number"
                                />
                            </div>
                        </div>
                        {isOtpSent && (
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">
                                    OTP
                                </label>
                                <input
                                    type="text"
                                    value={otp}
                                    onChange={(e) => setOtp(e.target.value)}
                                    className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter OTP"
                                    maxLength={6}
                                />
                            </div>
                        )}
                        <button
                            onClick={isOtpSent ? handleVerifyOTP : handleSendOTP}
                            disabled={loading}
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                        >
                            {loading
                                ? 'Processing...'
                                : isOtpSent
                                    ? 'Verify OTP'
                                    : 'Send OTP'}
                        </button>
                    </div>
                )}

                {/* Step 3: Aadhaar Verification */}
                {authStep === 3 && (
                    <div className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Aadhaar Number
                            </label>
                            <div className="relative">
                                <CreditCard className="absolute left-3 top-3 text-gray-400 h-5 w-5" />
                                <input
                                    type="text"
                                    value={aadhaar}
                                    onChange={(e) => setAadhaar(e.target.value)}
                                    className="pl-10 w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                                    placeholder="Enter Aadhaar number"
                                    maxLength={12}
                                />
                            </div>
                        </div>
                        <button
                            onClick={handleAadhaarVerification}
                            disabled={loading}
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                        >
                            {loading ? 'Verifying...' : 'Verify Aadhaar'}
                        </button>
                    </div>
                )}

                {/* Step 4: Face Recognition */}
                {authStep === 4 && (
                    <div className="space-y-4">
                        <div className="relative w-full h-64 bg-gray-100 rounded-lg overflow-hidden">
                            {capturedImage ? (
                                <img
                                    src={capturedImage}
                                    alt="Captured face"
                                    className="w-full h-full object-cover"
                                />
                            ) : (
                                <video
                                    ref={videoRef}
                                    autoPlay
                                    playsInline
                                    muted
                                    className="w-full h-full object-cover"
                                />
                            )}
                        </div>
                        {!stream && !capturedImage && (
                            <button
                                onClick={startCamera}
                                className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                            >
                                <Camera className="inline mr-2 h-5 w-5" />
                                Start Camera
                            </button>
                        )}
                        {stream && !capturedImage && (
                            <button
                                onClick={captureImage}
                                className="w-full bg-green-600 text-white p-2 rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2"
                            >
                                Capture Image
                            </button>
                        )}
                        {capturedImage && (
                            <div className="space-y-2">
                                <button
                                    onClick={handleFaceVerification}
                                    disabled={loading}
                                    className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
                                >
                                    {loading ? 'Verifying...' : 'Complete Verification'}
                                </button>
                                <button
                                    onClick={() => {
                                        setCapturedImage(null);
                                        startCamera();
                                    }}
                                    className="w-full bg-gray-200 text-gray-700 p-2 rounded-lg hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
                                >
                                    Retake Photo
                                </button>
                            </div>
                        )}
                    </div>
                )}

                {/* Back Button */}
                {authStep > 1 && (
                    <button
                        onClick={() => {
                            setAuthStep(authStep - 1);
                            if (authStep === 4) {
                                stopCamera();
                                setCapturedImage(null);
                            }
                        }}
                        className="mt-4 w-full bg-gray-100 text-gray-700 p-2 rounded-lg hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
                    >
                        Back
                    </button>
                )}

                {/* Authentication Steps Guide */}
                <div className="mt-6 text-sm text-gray-600">
                    <h3 className="font-medium mb-2">Authentication Steps:</h3>
                    <ol className="list-decimal list-inside space-y-1">
                        <li className={authStep === 1 ? 'text-blue-600' : ''}>
                            Email and Password verification
                        </li>
                        <li className={authStep === 2 ? 'text-blue-600' : ''}>
                            Mobile OTP verification
                        </li>
                        <li className={authStep === 3 ? 'text-blue-600' : ''}>
                            Aadhaar verification
                        </li>
                        <li className={authStep === 4 ? 'text-blue-600' : ''}>
                            Face recognition
                        </li>
                    </ol>
                </div>

                {/* Help Text */}
                <div className="mt-4 text-xs text-gray-500">
                    {authStep === 1 && (
                        <p>Enter your registered email and password to continue.</p>
                    )}
                    {authStep === 2 && (
                        <p>Enter your mobile number to receive a one-time password (OTP).</p>
                    )}
                    {authStep === 3 && (
                        <p>Please enter your 12-digit Aadhaar number for verification.</p>
                    )}
                    {authStep === 4 && (
                        <p>
                            Position your face within the frame and ensure good lighting for
                            accurate verification.
                        </p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Login;
