import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Webcam from "react-webcam";
import * as faceapi from 'face-api.js';
import "./Auth.css";

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    const [formData, setFormData] = useState({
        email: "",
        password: "",
        otp: ""
    });

    const [showOtp, setShowOtp] = useState(false);
    const [useFaceAuth, setUseFaceAuth] = useState(false);
    const [faceDetected, setFaceDetected] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState("");

    useEffect(() => {
        if (useFaceAuth) {
            loadFaceDetectionModels();
        }
    }, [useFaceAuth]);

    const loadFaceDetectionModels = async () => {
        try {
            await faceapi.nets.tinyFaceDetector.loadFromUri('/models');
            await faceapi.nets.faceLandmark68Net.loadFromUri('/models');
            await faceapi.nets.faceRecognitionNet.loadFromUri('/models');
        } catch (error) {
            console.error("Error loading face detection models:", error);
            setError("Failed to load face detection. Please try again.");
        }
    };

    const handleFaceDetection = async () => {
        if (webcamRef.current) {
            const video = webcamRef.current.video;
            try {
                const detection = await faceapi
                    .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
                    .withFaceLandmarks();

                if (detection) {
                    setFaceDetected(true);
                    // Capture face data for verification
                    const faceData = webcamRef.current.getScreenshot();
                    return faceData;
                }
            } catch (error) {
                console.error("Face detection error:", error);
                setError("Face detection failed. Please try again.");
            }
        }
        return null;
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
        setError(""); // Clear any previous errors
    };

    const requestOtp = async () => {
        setIsLoading(true);
        try {
            const response = await fetch('/api/auth/request-otp', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email: formData.email }),
            });

            if (response.ok) {
                setShowOtp(true);
                setError("");
            } else {
                const data = await response.json();
                setError(data.message || "Failed to send OTP");
            }
        } catch (error) {
            setError("Failed to send OTP. Please try again.");
        } finally {
            setIsLoading(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError("");

        try {
            // Collect authentication data
            const authData = {
                email: formData.email,
                password: formData.password,
                otp: showOtp ? formData.otp : undefined,
                faceData: useFaceAuth ? await handleFaceDetection() : undefined,
            };

            // Send authentication request
            const response = await fetch('/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(authData),
            });

            if (response.ok) {
                const data = await response.json();
                localStorage.setItem('token', data.token);
                navigate("/dashboard");
            } else {
                const data = await response.json();
                setError(data.message || "Login failed");
            }
        } catch (error) {
            setError("Login failed. Please try again.");
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Login</h2>
                {error && <div className="error-message">{error}</div>}
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            placeholder="Enter your email"
                            value={formData.email}
                            onChange={handleInputChange}
                            disabled={isLoading}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            name="password"
                            placeholder="Enter your password"
                            value={formData.password}
                            onChange={handleInputChange}
                            disabled={isLoading}
                        />
                    </div>

                    {showOtp && (
                        <div className="form-group">
                            <label htmlFor="otp">OTP</label>
                            <input
                                type="text"
                                id="otp"
                                name="otp"
                                placeholder="Enter OTP"
                                value={formData.otp}
                                onChange={handleInputChange}
                                disabled={isLoading}
                            />
                        </div>
                    )}

                    <div className="form-group checkbox">
                        <label>
                            <input
                                type="checkbox"
                                checked={useFaceAuth}
                                onChange={(e) => setUseFaceAuth(e.target.checked)}
                                disabled={isLoading}
                            />
                            Use Face Authentication
                        </label>
                    </div>

                    {useFaceAuth && (
                        <div className="webcam-container">
                            <Webcam
                                ref={webcamRef}
                                screenshotFormat="image/jpeg"
                                className="webcam"
                            />
                            {faceDetected && (
                                <div className="face-detected">
                                    Face Detected ✓
                                </div>
                            )}
                        </div>
                    )}

                    {!showOtp && (
                        <button
                            type="button"
                            className="auth-button secondary"
                            onClick={requestOtp}
                            disabled={!formData.email || isLoading}
                        >
                            {isLoading ? "Sending..." : "Send OTP"}
                        </button>
                    )}

                    <button
                        className="auth-button"
                        type="submit"
                        disabled={isLoading || (showOtp && !formData.otp)}
                    >
                        {isLoading ? "Logging in..." : "Login"}
                    </button>
                </form>
                <button
                    className="link-button"
                    onClick={() => navigate("/signup")}
                    disabled={isLoading}
                >
                    Don't have an account? Sign Up
                </button>
            </div>
        </div>
    );
};

export default Login;
