import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Webcam from "react-webcam";
import * as faceapi from 'face-api.js';
import "./Auth.css";

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    // State for form data
    const [formData, setFormData] = useState({
        aadharNumber: "",
        phone: "",
        otp: ""
    });

    // Authentication flow states
    const [authStep, setAuthStep] = useState('aadhar'); // aadhar -> phone -> face -> complete
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState("");
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);

    // Load face detection models
    useEffect(() => {
        if (authStep === 'face') {
            loadFaceDetectionModels();
        }
    }, [authStep]);

    const loadFaceDetectionModels = async () => {
        try {
            setIsLoading(true);
            await Promise.all([
                faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
                faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
                faceapi.nets.faceRecognitionNet.loadFromUri('/models'),
                faceapi.nets.faceExpressionNet.loadFromUri('/models')
            ]);
            setIsLoading(false);
        } catch (error) {
            setError("Failed to load face detection. Please try again.");
            setIsLoading(false);
        }
    };

    // Handle Aadhar verification
    const verifyAadhar = async () => {
        if (!formData.aadharNumber || formData.aadharNumber.length !== 12) {
            setError("Please enter a valid 12-digit Aadhar number");
            return;
        }

        setIsLoading(true);
        try {
            // Simulate API call to verify Aadhar
            const response = await fetch('/api/auth/verify-aadhar', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    aadharNumber: formData.aadharNumber
                }),
            });

            if (response.ok) {
                const data = await response.json();
                setFormData(prev => ({
                    ...prev,
                    phone: data.phone // Pre-fill phone from Aadhar data
                }));
                setAuthStep('phone');
                setError("");
            } else {
                throw new Error("Invalid Aadhar number");
            }
        } catch (error) {
            setError(error.message || "Aadhar verification failed");
        } finally {
            setIsLoading(false);
        }
    };

    // Handle phone OTP verification
    const sendOTP = async () => {
        if (!formData.phone || formData.phone.length !== 10) {
            setError("Please enter a valid 10-digit phone number");
            return;
        }

        setIsLoading(true);
        try {
            // Simulate API call to send OTP
            const response = await fetch('/api/auth/send-otp', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    phone: formData.phone
                }),
            });

            if (response.ok) {
                alert(`OTP sent to ${formData.phone}`);
                setError("");
            } else {
                throw new Error("Failed to send OTP");
            }
        } catch (error) {
            setError(error.message);
        } finally {
            setIsLoading(false);
        }
    };

    // Verify OTP and proceed to face recognition
    const verifyOTP = async () => {
        if (!formData.otp || formData.otp.length !== 6) {
            setError("Please enter valid 6-digit OTP");
            return;
        }

        setIsLoading(true);
        try {
            // Simulate API call to verify OTP
            const response = await fetch('/api/auth/verify-otp', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    phone: formData.phone,
                    otp: formData.otp
                }),
            });

            if (response.ok) {
                setAuthStep('face');
                setError("");
            } else {
                throw new Error("Invalid OTP");
            }
        } catch (error) {
            setError(error.message);
        } finally {
            setIsLoading(false);
        }
    };

    // Handle face detection and matching
    const handleFaceDetection = async () => {
        if (!webcamRef.current) return;

        try {
            const video = webcamRef.current.video;
            const detection = await faceapi
                .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
                .withFaceLandmarks()
                .withFaceExpressions();

            if (detection) {
                setFaceDetected(true);
                // Simulate face matching with Aadhar database
                const faceData = webcamRef.current.getScreenshot();
                await verifyFaceMatch(faceData);
            }
        } catch (error) {
            setError("Face detection failed. Please try again.");
        }
    };

    // Verify face match with Aadhar database
    const verifyFaceMatch = async (faceData) => {
        try {
            // Simulate API call to verify face match
            const response = await fetch('/api/auth/verify-face', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    aadharNumber: formData.aadharNumber,
                    faceData: faceData
                }),
            });

            if (response.ok) {
                setFaceMatched(true);
                setTimeout(() => navigate("/dashboard"), 1000);
            } else {
                throw new Error("Face verification failed");
            }
        } catch (error) {
            setError(error.message);
            setFaceMatched(false);
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
        setError("");
    };

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Secure Login</h2>
                {error && <div className="error-message">{error}</div>}

                {authStep === 'aadhar' && (
                    <div className="form-group">
                        <label>Aadhar Number</label>
                        <input
                            type="text"
                            name="aadharNumber"
                            value={formData.aadharNumber}
                            onChange={handleInputChange}
                            placeholder="Enter 12-digit Aadhar number"
                            maxLength={12}
                            disabled={isLoading}
                        />
                        <button
                            className="auth-button"
                            onClick={verifyAadhar}
                            disabled={isLoading || formData.aadharNumber.length !== 12}
                        >
                            {isLoading ? "Verifying..." : "Verify Aadhar"}
                        </button>
                    </div>
                )}

                {authStep === 'phone' && (
                    <div className="form-group">
                        <label>Phone Number</label>
                        <input
                            type="text"
                            name="phone"
                            value={formData.phone}
                            onChange={handleInputChange}
                            placeholder="Enter phone number"
                            maxLength={10}
                            disabled={isLoading}
                        />
                        <label>OTP</label>
                        <input
                            type="text"
                            name="otp"
                            value={formData.otp}
                            onChange={handleInputChange}
                            placeholder="Enter 6-digit OTP"
                            maxLength={6}
                            disabled={isLoading}
                        />
                        <button
                            className="auth-button secondary"
                            onClick={sendOTP}
                            disabled={isLoading}
                        >
                            {isLoading ? "Sending..." : "Send OTP"}
                        </button>
                        <button
                            className="auth-button"
                            onClick={verifyOTP}
                            disabled={isLoading || formData.otp.length !== 6}
                        >
                            {isLoading ? "Verifying..." : "Verify OTP"}
                        </button>
                    </div>
                )}

                {authStep === 'face' && (
                    <div className="face-auth-container">
                        <h3>Face Verification</h3>
                        <div className="webcam-container">
                            <Webcam
                                ref={webcamRef}
                                screenshotFormat="image/jpeg"
                                className="webcam"
                                onUserMedia={() => handleFaceDetection()}
                            />
                            {faceDetected && (
                                <div className={`face-status ${faceMatched ? 'matched' : ''}`}>
                                    {faceMatched ? 'Face Verified ✓' : 'Detecting Face...'}
                                </div>
                            )}
                        </div>
                        {faceMatched && (
                            <div className="success-message">
                                Authentication successful! Redirecting...
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
