import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { FaRegCalendarAlt, FaRegFlag, FaRegCommentDots, FaUserCircle, FaChartBar } from "react-icons/fa";
import "./Dashboard.css";

const STATUS_COLORS = {
    Active: "#4CAF50",
    Closed: "#F44336",
    Upcoming: "#2196F3",
    Default: "#9E9E9E"
};

const NEWS_API_KEY = "8280846cdd1a49ef880809767d0e61c8";

const Dashboard = () => {
    const navigate = useNavigate();
    const chatEndRef = useRef(null);

    // States
    const [elections, setElections] = useState([
        {
            id: 1,
            name: "Student Council Election",
            status: "Active",
            description: "Vote for your student representatives",
            endDate: "2024-12-20",
            candidates: [
                { id: 1, name: "John Doe", position: "President", votes: 145 },
                { id: 2, name: "Jane Smith", position: "Vice President", votes: 120 },
            ]
        },
        {
            id: 2,
            name: "Company Representative Election",
            status: "Closed",
            description: "Annual company board selection",
            endDate: "2024-11-30",
            candidates: [
                { id: 3, name: "Mike Johnson", position: "Board Member", votes: 89 },
                { id: 4, name: "Sarah Williams", position: "Board Member", votes: 92 },
            ]
        },
    ]);

    const [selectedElection, setSelectedElection] = useState(null);
    const [news, setNews] = useState([]);
    const [isLoadingNews, setIsLoadingNews] = useState(true);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState("");
    const [showCandidates, setShowCandidates] = useState(false);
    const [chatMessages, setChatMessages] = useState([
        { id: 1, user: "System", message: "Welcome to the election discussion!", timestamp: new Date() },
    ]);
    const [newMessage, setNewMessage] = useState("");

    // Fetch news effect
    useEffect(() => {
        const fetchNews = async () => {
            setIsLoadingNews(true);
            try {
                const response = await fetch(
                    `https://newsapi.org/v2/everything?q=election&language=en&sortBy=publishedAt&pageSize=6&apiKey=${NEWS_API_KEY}`
                );
                const data = await response.json();

                if (data.articles) {
                    const formattedNews = data.articles.map((article, index) => ({
                        id: index + 1,
                        title: article.title,
                        description: article.description || "No description available",
                        imageUrl: article.urlToImage || "https://via.placeholder.com/350x200",
                        url: article.url,
                        source: article.source.name,
                        publishedAt: new Date(article.publishedAt).toLocaleDateString()
                    }));
                    setNews(formattedNews);
                }
            } catch (error) {
                console.error("Error fetching news:", error);
                setNews([{
                    id: 1,
                    title: "Election Updates",
                    description: "Stay informed about the latest election news",
                    imageUrl: "https://via.placeholder.com/350x200",
                    source: "Local News",
                    publishedAt: new Date().toLocaleDateString()
                }]);
            } finally {
                setIsLoadingNews(false);
            }
        };

        fetchNews();
    }, []);

    // Scroll chat to bottom effect
    useEffect(() => {
        chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatMessages]);

    // Functions
    const addElection = () => {
        const newElection = {
            id: elections.length + 1,
            name: `New Election ${elections.length + 1}`,
            status: "Upcoming",
            description: "Click to add description",
            endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
            candidates: []
        };
        setElections(prevElections => [...prevElections, newElection]);
    };

    const handleElectionClick = (election) => {
        setSelectedElection(election);
        setShowCandidates(true);
    };

    const handleAddComment = () => {
        if (!newComment.trim()) return;
        const commentToAdd = {
            id: comments.length + 1,
            text: newComment.trim(),
            user: "Current User",
            timestamp: new Date(),
            likes: 0
        };
        setComments(prevComments => [...prevComments, commentToAdd]);
        setNewComment("");
    };

    const handleSendMessage = () => {
        if (!newMessage.trim()) return;
        const messageToAdd = {
            id: chatMessages.length + 1,
            user: "Current User",
            message: newMessage.trim(),
            timestamp: new Date()
        };
        setChatMessages(prevMessages => [...prevMessages, messageToAdd]);
        setNewMessage("");
    };

    const handleLikeComment = (commentId) => {
        setComments(prevComments =>
            prevComments.map(comment =>
                comment.id === commentId
                    ? { ...comment, likes: (comment.likes || 0) + 1 }
                    : comment
            )
        );
    };

    const getStatusColor = (status) => {
        return STATUS_COLORS[status] || STATUS_COLORS.Default;
    };

    return (
        <div className="dashboard-container">
            <header>
                <h1>Decentralized Voting System Dashboard</h1>
            </header>

            <div className="dashboard-content">
                {/* Elections Section */}
                <section className="election-section">
                    <div className="section-header">
                        <h2>Available Elections</h2>
                        <button className="auth-button add-election-btn" onClick={addElection}>
                            Add Election
                        </button>
                    </div>

                    <div className="election-cards">
                        {elections.map((election) => (
                            <div
                                key={election.id}
                                className="election-card interactive"
                                onClick={() => handleElectionClick(election)}
                                style={{
                                    borderLeft: `5px solid ${getStatusColor(election.status)}`
                                }}
                            >
                                <div className="election-info">
                                    <h3>{election.name}</h3>
                                    <p className="description">{election.description}</p>
                                    <p className="end-date">Ends: {election.endDate}</p>
                                    <p className="status" style={{ color: getStatusColor(election.status) }}>
                                        {election.status}
                                    </p>
                                </div>
                                <FaRegCalendarAlt
                                    className="icon"
                                    color={getStatusColor(election.status)}
                                    size={24}
                                />
                            </div>
                        ))}
                    </div>
                </section>

                {/* News Section */}
                <section className="news-section">
                    <h2>Latest Election News</h2>
                    <div className="news-cards">
                        {isLoadingNews ? (
                            <div className="loading">Loading news...</div>
                        ) : (
                            news.map((item) => (
                                <div
                                    className="news-card"
                                    key={item.id}
                                    onClick={() => window.open(item.url, '_blank')}
                                >
                                    <img
                                        src={item.imageUrl}
                                        alt={`News about ${item.title}`}
                                        className="news-image"
                                    />
                                    <div className="news-content">
                                        <h3>{item.title}</h3>
                                        <p>{item.description}</p>
                                        <div className="news-meta">
                                            <span>{item.source}</span>
                                            <span>{item.publishedAt}</span>
                                        </div>
                                    </div>
                                </div>
                            ))
                        )}
                    </div>
                </section>

                {/* Chat Section */}
                <section className="chat-section">
                    <h2>Live Discussion</h2>
                    <div className="chat-container">
                        <div className="chat-messages">
                            {chatMessages.map((msg) => (
                                <div key={msg.id} className={`chat-message ${msg.user === "System" ? "system" : ""}`}>
                                    <strong>{msg.user}:</strong>
                                    <p>{msg.message}</p>
                                    <small>{new Date(msg.timestamp).toLocaleTimeString()}</small>
                                </div>
                            ))}
                            <div ref={chatEndRef} />
                        </div>
                        <div className="chat-input">
                            <input
                                type="text"
                                value={newMessage}
                                onChange={(e) => setNewMessage(e.target.value)}
                                placeholder="Type your message..."
                                onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                            />
                            <button onClick={handleSendMessage} className="auth-button">
                                Send
                            </button>
                        </div>
                    </div>
                </section>

                {/* Comments Section */}
                <section className="comments-section">
                    <h2>Voter Opinions</h2>
                    <div className="comments-display">
                        {comments.map((comment) => (
                            <div className="comment-card" key={comment.id}>
                                <div className="comment-header">
                                    <FaUserCircle className="user-avatar" />
                                    <span className="username">{comment.user}</span>
                                    <span className="timestamp">
                                        {new Date(comment.timestamp).toLocaleString()}
                                    </span>
                                </div>
                                <p>{comment.text}</p>
                                <div className="comment-actions">
                                    <button
                                        className="like-button"
                                        onClick={() => handleLikeComment(comment.id)}
                                    >
                                        üëç {comment.likes || 0}
                                    </button>
                                    <FaRegCommentDots className="icon" />
                                </div>
                            </div>
                        ))}
                    </div>
                    <div className="add-comment">
                        <input
                            type="text"
                            value={newComment}
                            onChange={(e) => setNewComment(e.target.value)}
                            placeholder="Share your opinion..."
                            className="comment-input"
                            onKeyPress={(e) => e.key === 'Enter' && handleAddComment()}
                        />
                        <button
                            onClick={handleAddComment}
                            className="auth-button"
                            disabled={!newComment.trim()}
                        >
                            Add Opinion
                        </button>
                    </div>
                </section>

                {/* Candidates Modal */}
                {showCandidates && selectedElection && (
                    <div className="modal">
                        <div className="modal-content">
                            <h2>{selectedElection.name} - Candidates</h2>
                            <div className="candidates-grid">
                                {selectedElection.candidates.map(candidate => (
                                    <div key={candidate.id} className="candidate-card">
                                        <FaUserCircle size={50} className="candidate-avatar" />
                                        <h3>{candidate.name}</h3>
                                        <p>{candidate.position}</p>
                                        {selectedElection.status === "Closed" && (
                                            <div className="votes-info">
                                                <FaChartBar /> {candidate.votes} votes
                                            </div>
                                        )}
                                    </div>
                                ))}
                            </div>
                            <button className="auth-button" onClick={() => setShowCandidates(false)}>
                                Close
                            </button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Dashboard;
