import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import * as faceapi from 'face-api.js';
import './Auth.css';

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    const [modelsLoaded, setModelsLoaded] = useState(false);
    const [authStep, setAuthStep] = useState('credentials');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        aadharNumber: ''
    });

    const loadModels = async () => {
        const MODEL_URL = `${window.location.origin}/models`;
        try {
            await faceapi.nets.tinyFaceDetector.loadFromUri(MODEL_URL);
            await faceapi.nets.faceLandmark68Net.loadFromUri(MODEL_URL);
            await faceapi.nets.faceRecognitionNet.loadFromUri(MODEL_URL);
            return true;
        } catch (error) {
            console.error('FATAL MODEL ERROR:', error);
            return false;
        }
    };

    useEffect(() => {
        (async () => {
            setLoading(true);
            const success = await loadModels();
            if (success) {
                setModelsLoaded(true);
            } else {
                setError('Failed to load face detection models');
            }
            setLoading(false);
            setIsRegistering(!localStorage.getItem('faceDescriptor'));
        })();

        return () => {
            if (webcamRef.current?.video) {
                const stream = webcamRef.current.video.srcObject;
                if (stream) {
                    stream.getTracks().forEach(track => track.stop());
                }
            }
        };
    }, []);

  const captureAndStoreFace = async () => {
    if (!webcamRef.current) return false;

    try {
        const detections = await faceapi.detectSingleFace(
            webcamRef.current.video,
            new faceapi.TinyFaceDetectorOptions()
        ).withFaceLandmarks().withFaceDescriptor();

        if (detections) {
            localStorage.setItem('faceDescriptor', JSON.stringify(Array.from(detections.descriptor)));
            setIsRegistering(false);  // Add this line to exit registration mode
            return true;
        }
    } catch (error) {
        console.error('Error capturing face:', error);
        setError('Failed to capture face');
    }
    return false;
};

    const verifyFace = async () => {
        if (!webcamRef.current) return false;

        try {
            const storedDescriptor = new Float32Array(JSON.parse(localStorage.getItem('faceDescriptor')));
            const detection = await faceapi.detectSingleFace(
                webcamRef.current.video,
                new faceapi.TinyFaceDetectorOptions()
            ).withFaceLandmarks().withFaceDescriptor();

            if (detection) {
                const distance = faceapi.euclideanDistance(detection.descriptor, storedDescriptor);
                return distance < 0.6;
            }
        } catch (error) {
            console.error('Verification error:', error);
            setError('Face verification failed');
        }
        return false;
    };

    const handleFaceDetection = async () => {
        if (!webcamRef.current?.video || !modelsLoaded) return;

        const checkFace = async () => {
            try {
                if (isRegistering) {
                    const registered = await captureAndStoreFace();
                    if (registered) {
                        setFaceDetected(true);
                        setFaceMatched(true);
                        setTimeout(() => navigate('/dashboard'), 1500);
                    }
                } else {
                    const isMatch = await verifyFace();
                    if (isMatch) {
                        setFaceDetected(true);
                        setFaceMatched(true);
                        setTimeout(() => navigate('/dashboard'), 1500);
                    }
                }
            } catch (error) {
                console.error('Face detection error:', error);
                setError('Face detection failed');
            }
        };

        const interval = setInterval(checkFace, 1000);
        return () => clearInterval(interval);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'aadharNumber') {
            const numericValue = value.replace(/\D/g, '');
            setFormData(prev => ({ ...prev, [name]: numericValue }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
        setError('');
    };

    const validateEmail = (email) => {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    };

    const handleCredentialsSubmit = async (e) => {
        e.preventDefault();
        if (!validateEmail(formData.email)) {
            setError('Please enter a valid email');
            return;
        }
        if (formData.password.length < 6) {
            setError('Password must be at least 6 characters');
            return;
        }

        setLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep('aadhar');
        } catch (error) {
            setError('Authentication failed');
        } finally {
            setLoading(false);
        }
    };

    const handleAadharSubmit = async () => {
        if (formData.aadharNumber.length !== 12) {
            setError('Please enter a valid 12-digit Aadhar number');
            return;
        }

        setLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep('face');
        } catch (error) {
            setError('Aadhar verification failed');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Secure Login</h2>
                {error && <div className="error-message">{error}</div>}

                {authStep === 'credentials' && (
                    <form onSubmit={handleCredentialsSubmit}>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="Enter your email"
                                disabled={loading}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="Enter your password"
                                disabled={loading}
                            />
                        </div>
                        <button
                            className="auth-button"
                            type="submit"
                            disabled={loading}
                        >
                            {loading ? "Verifying..." : "Next"}
                        </button>
                    </form>
                )}

                {authStep === 'aadhar' && (
                    <div className="form-group">
                        <label htmlFor="aadhar">Aadhar Number</label>
                        <input
                            type="text"
                            id="aadhar"
                            name="aadharNumber"
                            value={formData.aadharNumber}
                            onChange={handleInputChange}
                            placeholder="Enter 12-digit Aadhar number"
                            maxLength={12}
                            disabled={loading}
                        />
                        <button
                            className="auth-button"
                            onClick={handleAadharSubmit}
                            disabled={loading || formData.aadharNumber.length !== 12}
                        >
                            {loading ? "Verifying..." : "Verify Aadhar"}
                        </button>
                        <button
                            className="auth-button secondary"
                            onClick={() => setAuthStep('credentials')}
                            disabled={loading}
                        >
                            Back
                        </button>
                    </div>
                )}

                {authStep === 'face' && (
                    <div className="face-auth-container">
                        <h3>{isRegistering ? 'Register Face' : 'Verify Face'}</h3>
                        <div className="webcam-container">
                            <Webcam
                                ref={webcamRef}
                                screenshotFormat="image/jpeg"
                                className="webcam"
                                onUserMedia={handleFaceDetection}
                            />
                            {faceDetected && (
                                <div className={`face-status ${faceMatched ? 'matched' : ''}`}>
                                    {faceMatched
                                        ? `Face ${isRegistering ? 'Registered' : 'Verified'} ✓`
                                        : 'Detecting Face...'}
                                </div>
                            )}
                        </div>
                        {faceMatched && (
                            <div className="success-message">
                                Authentication successful! Redirecting...
                            </div>
                        )}
                        <button
                            className="auth-button secondary"
                            onClick={() => setAuthStep('aadhar')}
                            disabled={loading}
                        >
                            Back
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
