import React, { useState, useEffect } from "react";
import "./User.css";

function User() {
    const [news, setNews] = useState([]);
    const [comment, setComment] = useState("");
    const [comments, setComments] = useState(["Great article!", "Very insightful."]);
    const [voteCount, setVoteCount] = useState(0);

    // Fetch news from API
    useEffect(() => {
        fetch("https://newsapi.org/v2/top-headlines?country=us&apiKey=YOUR_API_KEY")
            .then((response) => response.json())
            .then((data) => setNews(data.articles.slice(0, 5))) // Limit to 5 articles
            .catch((error) => console.error("Error fetching news:", error));
    }, []);

    const handleVote = () => {
        setVoteCount(voteCount + 1);
    };

    const handleAddComment = () => {
        if (comment.trim()) {
            setComments([...comments, comment]);
            setComment("");
        }
    };

    return (
        <div className="user-profile">
            <div className="profile-header">
                <h1>Welcome, User!</h1>
                <p>Explore the latest news and engage with the community!</p>
            </div>

            <div className="profile-content">
                <h2>News</h2>
                <ul className="news-list">
                    {news.map((article, index) => (
                        <li key={index} className="news-item">
                            <h3>{article.title}</h3>
                            <p>{article.description}</p>
                            <a href={article.url} target="_blank" rel="noopener noreferrer">
                                Read More
                            </a>
                        </li>
                    ))}
                </ul>

                <h2>Vote</h2>
                <p>Votes: {voteCount}</p>
                <button className="vote-button" onClick={handleVote}>
                    Vote for this content
                </button>

                <h2>Comments</h2>
                <div className="comment-box">
                    <textarea
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        placeholder="Write your comment here..."
                    ></textarea>
                    <button className="comment-button" onClick={handleAddComment}>
                        Add Comment
                    </button>
                </div>
                <div className="comments-list">
                    <h3>Community Comments</h3>
                    <ul>
                        {comments.map((cmt, idx) => (
                            <li key={idx}>{cmt}</li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default User;
