import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Webcam from "react-webcam";
import * as faceapi from 'face-api.js';
import "./Auth.css";

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    // State for form data
    const [formData, setFormData] = useState({
        email: "",
        password: "",
        aadharNumber: "",
    });

    // Authentication flow states
    const [authStep, setAuthStep] = useState('credentials'); // credentials -> aadhar -> face
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState("");
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);

    // Validate Aadhar number using Verhoeff algorithm
    const validateAadhar = (aadhar) => {
        // Basic validation for 12 digits
        if (!/^\d{12}$/.test(aadhar)) return false;

        // Simple checksum validation (this is a basic example)
        let sum = 0;
        for (let i = 0; i < 12; i++) {
            sum += parseInt(aadhar[i]);
        }
        return sum % 10 === 0;
    };

    // Handle initial login
    const handleCredentialsSubmit = async () => {
        if (!formData.email || !formData.password) {
            setError("Please enter both email and password");
            return;
        }

        setIsLoading(true);
        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1000));

            // In real application, verify credentials with backend
            setAuthStep('aadhar');
            setError("");
        } catch (error) {
            setError("Invalid credentials");
        } finally {
            setIsLoading(false);
        }
    };

    // Handle Aadhar verification
    const handleAadharSubmit = async () => {
        if (!validateAadhar(formData.aadharNumber)) {
            setError("Invalid Aadhar number");
            return;
        }

        setIsLoading(true);
        try {
            // Simulate API call
            await new Promise(resolve => setTimeout(resolve, 1000));

            setAuthStep('face');
            setError("");
            // Load face detection models when moving to face step
            await loadFaceDetectionModels();
        } catch (error) {
            setError("Aadhar verification failed");
        } finally {
            setIsLoading(false);
        }
    };

    // Load face detection models
    const loadFaceDetectionModels = async () => {
        try {
            await Promise.all([
                faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
                faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
                faceapi.nets.faceRecognitionNet.loadFromUri('/models')
            ]);
        } catch (error) {
            setError("Failed to load face detection. Please refresh and try again.");
        }
    };

    // Handle face detection
    const handleFaceDetection = async () => {
        if (!webcamRef.current) return;

        const interval = setInterval(async () => {
            try {
                const video = webcamRef.current.video;
                if (video) {
                    const detection = await faceapi
                        .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
                        .withFaceLandmarks();

                    if (detection) {
                        setFaceDetected(true);
                        clearInterval(interval);
                        // Simulate face verification
                        setTimeout(() => {
                            setFaceMatched(true);
                            setTimeout(() => navigate("/dashboard"), 1000);
                        }, 1500);
                    }
                }
            } catch (error) {
                console.error("Face detection error:", error);
            }
        }, 100);

        // Cleanup interval
        return () => clearInterval(interval);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value.replace(/\D/g, '') // Only allow digits for Aadhar
        }));
        setError("");
    };

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Secure Login</h2>
                {error && <div className="error-message">{error}</div>}

                {/* Step 1: Credentials */}
                {authStep === 'credentials' && (
                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            placeholder="Enter your email"
                            disabled={isLoading}
                        />
                        <label>Password</label>
                        <input
                            type="password"
                            name="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            placeholder="Enter your password"
                            disabled={isLoading}
                        />
                        <button
                            className="auth-button"
                            onClick={handleCredentialsSubmit}
                            disabled={isLoading}
                        >
                            {isLoading ? "Verifying..." : "Next"}
                        </button>
                        <button
                            className="link-button"
                            onClick={() => navigate("/signup")}
                            disabled={isLoading}
                        >
                            Don't have an account? Sign Up
                        </button>
                    </div>
                )}

                {/* Step 2: Aadhar */}
                {authStep === 'aadhar' && (
                    <div className="form-group">
                        <label>Aadhar Number</label>
                        <input
                            type="text"
                            name="aadharNumber"
                            value={formData.aadharNumber}
                            onChange={handleInputChange}
                            placeholder="Enter 12-digit Aadhar number"
                            maxLength={12}
                            disabled={isLoading}
                        />
                        <button
                            className="auth-button"
                            onClick={handleAadharSubmit}
                            disabled={isLoading || formData.aadharNumber.length !== 12}
                        >
                            {isLoading ? "Verifying..." : "Verify Aadhar"}
                        </button>
                    </div>
                )}

                {/* Step 3: Face Recognition */}
                {authStep === 'face' && (
                    <div className="face-auth-container">
                        <h3>Face Verification</h3>
                        <div className="webcam-container">
                            <Webcam
                                ref={webcamRef}
                                screenshotFormat="image/jpeg"
                                className="webcam"
                                onUserMedia={handleFaceDetection}
                            />
                            {faceDetected && (
                                <div className={`face-status ${faceMatched ? 'matched' : ''}`}>
                                    {faceMatched ? 'Face Verified ✓' : 'Detecting Face...'}
                                </div>
                            )}
                        </div>
                        {faceMatched && (
                            <div className="success-message">
                                Authentication successful! Redirecting...
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
