import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import * as blazeface from '@tensorflow-models/blazeface';
import * as tf from '@tensorflow/tfjs';
import './Auth.css';

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    const [modelsLoaded, setModelsLoaded] = useState(false);
    const [authStep, setAuthStep] = useState('credentials');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);
    const [isRegistering, setIsRegistering] = useState(false);

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        aadharNumber: ''
    });

    useEffect(() => {
        (async () => {
            setLoading(true);
            await tf.setBackend('webgl'); // Improve performance using WebGL
            const model = await blazeface.load();
            if (model) {
                setModelsLoaded(true);
            } else {
                setError('Failed to load face detection models');
            }
            setLoading(false);
            setIsRegistering(!localStorage.getItem('faceDescriptor'));
        })();

        return () => {
            if (webcamRef.current?.video) {
                const stream = webcamRef.current.video.srcObject;
                if (stream) {
                    stream.getTracks().forEach(track => track.stop());
                }
            }
        };
    }, []);

    const captureAndStoreFace = async () => {
        if (!webcamRef.current || !modelsLoaded) return false;

        try {
            const model = await blazeface.load();
            const predictions = await model.estimateFaces(webcamRef.current.video, false);

            if (predictions.length > 0) {
                localStorage.setItem('faceDescriptor', JSON.stringify(predictions[0].landmarks));
                return true;
            }
        } catch (error) {
            console.error('Error capturing face:', error);
            setError('Failed to capture face');
        }
        return false;
    };

    const verifyFace = async () => {
        if (!webcamRef.current || !modelsLoaded) return false;

        try {
            const model = await blazeface.load();
            const storedDescriptor = JSON.parse(localStorage.getItem('faceDescriptor'));

            const predictions = await model.estimateFaces(webcamRef.current.video, false);

            if (predictions.length > 0) {
                const currentDescriptor = predictions[0].landmarks;

                const distance = tf.norm(tf.sub(tf.tensor(storedDescriptor), tf.tensor(currentDescriptor))).dataSync()[0];

                return distance < 10;
            }
        } catch (error) {
            console.error('Verification error:', error);
            setError('Face verification failed');
        }
        return false;
    };

    const handleFaceDetection = async () => {
        if (!webcamRef.current?.video || !modelsLoaded) return;

        const checkFace = async () => {
            try {
                if (isRegistering) {
                    const registered = await captureAndStoreFace();
                    if (registered) {
                        setFaceDetected(true);
                        setFaceMatched(true);
                        setTimeout(() => navigate('/dashboard'), 1500);
                    }
                } else {
                    const isMatch = await verifyFace();
                    if (isMatch) {
                        setFaceDetected(true);
                        setFaceMatched(true);
                        setTimeout(() => navigate('/dashboard'), 1500);
                    }
                }
            } catch (error) {
                console.error('Face detection error:', error);
                setError('Face detection failed');
            }
        };

        const interval = setInterval(checkFace, 1000);
        return () => clearInterval(interval);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'aadharNumber') {
            const numericValue = value.replace(/\D/g, '');
            setFormData(prev => ({ ...prev, [name]: numericValue }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
        setError('');
    };

    const validateEmail = (email) => {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    };

    const handleCredentialsSubmit = async (e) => {
        e.preventDefault();

        if (!validateEmail(formData.email)) {
            setError('Please enter a valid email');
            return;
        }
        if (formData.password.length < 6) {
            setError('Password must be at least 6 characters');
            return;
        }

        setLoading(true);
        console.log("Submitting credentials..."); // Debug log

        try {
            await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate API call
            console.log("Auth successful, moving to Aadhar step"); // Debug log
            setAuthStep('aadhar'); // Move to Aadhar step
        } catch (error) {
            setError('Authentication failed');
        } finally {
            setLoading(false);
        }
    };




    const handleAadharSubmit = async () => {
        if (formData.aadharNumber.length !== 12) {
            setError('Please enter a valid 12-digit Aadhar number');
            return;
        }

        setLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep('face');
        } catch (error) {
            setError('Aadhar verification failed');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Secure Login</h2>
                {error && <div className="error-message">{error}</div>}

                {authStep === 'credentials' && (
                    <form onSubmit={handleCredentialsSubmit}>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="Enter your email"
                                disabled={loading}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="Enter your password"
                                disabled={loading}
                            />
                        </div>
                        <button className="auth-button" type="submit" disabled={loading}>
                            {loading ? "Verifying..." : "Next"}
                        </button>
                    </form>
                )}

                {authStep === 'face' && (
                    <div className="face-auth-container">
                        <h3>{isRegistering ? 'Register Face' : 'Verify Face'}</h3>
                        <div className="webcam-container">
                            <Webcam ref={webcamRef} screenshotFormat="image/jpeg" className="webcam" onUserMedia={handleFaceDetection} />
                            {faceDetected && (
                                <div className={`face-status ${faceMatched ? 'matched' : ''}`}>
                                    {faceMatched ? `Face ${isRegistering ? 'Registered' : 'Verified'} âœ“` : 'Detecting Face...'}
                                </div>
                            )}
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
