import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import * as faceapi from 'face-api.js';

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    const [step, setStep] = useState('credentials');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        aadharNumber: '',
        voterId: ''
    });

    useEffect(() => {
        const loadFaceAPI = async () => {
            try {
                await Promise.all([
                    faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
                    faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
                    faceapi.nets.faceRecognitionNet.loadFromUri('/models')
                ]);
            } catch (error) {
                setError('Face detection initialization failed');
            }
        };
        loadFaceAPI();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        const numericValue = name === 'aadharNumber' ? value.replace(/\D/g, '') : value;
        setFormData(prev => ({ ...prev, [name]: numericValue }));
        setError('');
    };

    const validateCredentials = async () => {
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
            setError('Invalid email format');
            return false;
        }
        if (formData.password.length < 6) {
            setError('Password must be at least 6 characters');
            return false;
        }
        return true;
    };

    const validateAadhar = () => {
        return /^\d{12}$/.test(formData.aadharNumber);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            if (step === 'credentials') {
                if (await validateCredentials()) {
                    setStep('aadhar');
                }
            } else if (step === 'aadhar') {
                if (validateAadhar()) {
                    // API call to verify Aadhar
                    setStep('face');
                } else {
                    setError('Invalid Aadhar number');
                }
            }
        } catch (error) {
            setError('Authentication failed');
        } finally {
            setLoading(false);
        }
    };

    const handleFaceDetection = async () => {
        if (!webcamRef.current) return;

        const interval = setInterval(async () => {
            try {
                const video = webcamRef.current.video;
                if (video) {
                    const detection = await faceapi
                        .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
                        .withFaceLandmarks();

                    if (detection) {
                        setFaceDetected(true);
                        clearInterval(interval);

                        // Simulate face verification
                        setTimeout(() => {
                            setFaceMatched(true);
                            setTimeout(() => navigate('/dashboard'), 1000);
                        }, 1500);
                    }
                }
            } catch (error) {
                console.error('Face detection error:', error);
            }
        }, 100);

        return () => clearInterval(interval);
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-gray-100 flex items-center justify-center p-4">
            <div className="bg-white rounded-lg shadow-xl p-8 w-full max-w-md">
                <h1 className="text-2xl font-bold text-center text-gray-800 mb-8">
                    Secure Login
                </h1>

                {error && (
                    <div className="bg-red-50 text-red-500 p-3 rounded-lg mb-4">
                        {error}
                    </div>
                )}

                {step === 'credentials' && (
                    <form onSubmit={handleSubmit} className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Email
                            </label>
                            <input
                                type="email"
                                name="email"
                                className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                                value={formData.email}
                                onChange={handleInputChange}
                                disabled={loading}
                            />
                        </div>
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Password
                            </label>
                            <input
                                type="password"
                                name="password"
                                className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                                value={formData.password}
                                onChange={handleInputChange}
                                disabled={loading}
                            />
                        </div>
                        <button
                            type="submit"
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 disabled:opacity-50"
                            disabled={loading}
                        >
                            {loading ? 'Verifying...' : 'Continue'}
                        </button>
                    </form>
                )}

                {step === 'aadhar' && (
                    <form onSubmit={handleSubmit} className="space-y-4">
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-1">
                                Aadhar Number
                            </label>
                            <input
                                type="text"
                                name="aadharNumber"
                                className="w-full p-2 border rounded-lg focus:ring-2 focus:ring-blue-500"
                                value={formData.aadharNumber}
                                onChange={handleInputChange}
                                maxLength={12}
                                disabled={loading}
                            />
                        </div>
                        <button
                            type="submit"
                            className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700 disabled:opacity-50"
                            disabled={loading || formData.aadharNumber.length !== 12}
                        >
                            {loading ? 'Verifying...' : 'Verify Aadhar'}
                        </button>
                    </form>
                )}

                {step === 'face' && (
                    <div className="space-y-4">
                        <div className="relative w-full h-64 bg-gray-100 rounded-lg overflow-hidden">
                            <Webcam
                                ref={webcamRef}
                                screenshotFormat="image/jpeg"
                                className="absolute w-full h-full object-cover"
                                onUserMedia={handleFaceDetection}
                            />
                            {faceDetected && (
                                <div className={`absolute bottom-4 left-1/2 transform -translate-x-1/2 
                                    px-4 py-2 rounded-full text-white text-sm
                                    ${faceMatched ? 'bg-green-500' : 'bg-gray-800'}`}>
                                    {faceMatched ? 'Face Verified âœ“' : 'Detecting Face...'}
                                </div>
                            )}
                        </div>
                        {faceMatched && (
                            <div className="text-center text-green-600">
                                Authentication successful! Redirecting...
                            </div>
                        )}
                    </div>
                )}

                {step !== 'credentials' && (
                    <button
                        onClick={() => setStep(step === 'face' ? 'aadhar' : 'credentials')}
                        className="mt-4 w-full bg-gray-100 text-gray-700 p-2 rounded-lg hover:bg-gray-200"
                    >
                        Back
                    </button>
                )}
            </div>
        </div>
    );
};

export default Login;
