import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import * as faceapi from 'face-api.js';
import './.css';

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    const [modelsLoaded, setModelsLoaded] = useState(false);
    const [authStep, setAuthStep] = useState('credentials');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);

    const [formData, setFormData] = useState({
        email: '',
        password: '',
        aadharNumber: ''
    });

    const loadModels = async () => {
        const MODEL_URL = `${process.env.PUBLIC_URL}/models`;
        try {
            await Promise.all([
                faceapi.nets.tinyFaceDetector.load(MODEL_URL),
                faceapi.nets.faceLandmark68Net.load(MODEL_URL),
                faceapi.nets.faceRecognitionNet.load(MODEL_URL)
            ]);
            return true;
        } catch (error) {
            console.error('Error loading models:', error);
            return false;
        }
    };

    useEffect(() => {
        (async () => {
            setLoading(true);
            const success = await loadModels();
            if (success) {
                setModelsLoaded(true);
            } else {
                setError('Failed to load face detection models');
            }
            setLoading(false);
        })();
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'aadharNumber') {
            const numericValue = value.replace(/\D/g, '');
            setFormData(prev => ({ ...prev, [name]: numericValue }));
        } else {
            setFormData(prev => ({ ...prev, [name]: value }));
        }
        setError('');
    };

    const validateEmail = (email) => {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    };

    const handleCredentialsSubmit = async (e) => {
        e.preventDefault();
        if (!validateEmail(formData.email)) {
            setError('Please enter a valid email');
            return;
        }
        if (formData.password.length < 6) {
            setError('Password must be at least 6 characters');
            return;
        }

        setLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep('aadhar');
        } catch (error) {
            setError('Authentication failed');
        } finally {
            setLoading(false);
        }
    };

    const handleAadharSubmit = async () => {
        if (formData.aadharNumber.length !== 12) {
            setError('Please enter a valid 12-digit Aadhar number');
            return;
        }

        setLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep('face');
        } catch (error) {
            setError('Aadhar verification failed');
        } finally {
            setLoading(false);
        }
    };

    const handleFaceDetection = async () => {
        if (!webcamRef.current?.video || !modelsLoaded) return;

        try {
            const detection = await faceapi.detectSingleFace(
                webcamRef.current.video,
                new faceapi.TinyFaceDetectorOptions()
            );

            if (detection) {
                setFaceDetected(true);
                setTimeout(() => {
                    setFaceMatched(true);
                    setTimeout(() => navigate('/dashboard'), 1000);
                }, 1500);
            }
        } catch (error) {
            console.error('Face detection error:', error);
            setError('Face detection failed');
        }
    };

    useEffect(() => {
        return () => {
            if (webcamRef.current?.video) {
                const stream = webcamRef.current.video.srcObject;
                if (stream) {
                    stream.getTracks().forEach(track => track.stop());
                }
            }
        };
    }, []);

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Secure Login</h2>
                {error && <div className="error-message">{error}</div>}

                {authStep === 'credentials' && (
                    <form onSubmit={handleCredentialsSubmit}>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={formData.email}
                                onChange={handleInputChange}
                                placeholder="Enter your email"
                                disabled={loading}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                value={formData.password}
                                onChange={handleInputChange}
                                placeholder="Enter your password"
                                disabled={loading}
                            />
                        </div>
                        <button
                            className="auth-button"
                            type="submit"
                            disabled={loading}
                        >
                            {loading ? "Verifying..." : "Next"}
                        </button>
                        <button
                            className="link-button"
                            onClick={() => navigate("/signup")}
                            type="button"
                            disabled={loading}
                        >
                            Don't have an account? Sign Up
                        </button>
                    </form>
                )}

                {authStep === 'aadhar' && (
                    <div className="form-group">
                        <label htmlFor="aadhar">Aadhar Number</label>
                        <input
                            type="text"
                            id="aadhar"
                            name="aadharNumber"
                            value={formData.aadharNumber}
                            onChange={handleInputChange}
                            placeholder="Enter 12-digit Aadhar number"
                            maxLength={12}
                            disabled={loading}
                        />
                        <button
                            className="auth-button"
                            onClick={handleAadharSubmit}
                            disabled={loading || formData.aadharNumber.length !== 12}
                        >
                            {loading ? "Verifying..." : "Verify Aadhar"}
                        </button>
                        <button
                            className="auth-button secondary"
                            onClick={() => setAuthStep('credentials')}
                            disabled={loading}
                        >
                            Back
                        </button>
                    </div>
                )}

                {authStep === 'face' && (
                    <div className="face-auth-container">
                        <div className="webcam-container">
                            <Webcam
                                ref={webcamRef}
                                screenshotFormat="image/jpeg"
                                className="webcam"
                                onUserMedia={handleFaceDetection}
                            />
                            {faceDetected && (
                                <div className="face-detected">
                                    {faceMatched ? 'Face Verified âœ“' : 'Detecting Face...'}
                                </div>
                            )}
                        </div>
                        {faceMatched && (
                            <div className="success-message">
                                Authentication successful! Redirecting...
                            </div>
                        )}
                        <button
                            className="auth-button secondary"
                            onClick={() => setAuthStep('aadhar')}
                            disabled={loading}
                        >
                            Back
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
