import React, { useState, useEffect } from "react";
import { FaRegCalendarAlt, FaRegFlag, FaRegCommentDots } from "react-icons/fa";
import axios from "axios"; // Install axios if not already added
import "./Dashboard.css";

const Dashboard = () => {
    const [elections, setElections] = useState([]);
    const [news, setNews] = useState([]);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState("");

    // Fetch elections and news from backend
    useEffect(() => {
        axios.get("http://localhost:5000/api/elections")
            .then((response) => setElections(response.data))
            .catch((error) => console.error("Error fetching elections:", error));

        axios.get("http://localhost:5000/api/news")
            .then((response) => setNews(response.data))
            .catch((error) => console.error("Error fetching news:", error));
    }, []);

    const addElection = () => {
        const newElection = { name: "New Election", status: "Upcoming" };
        axios.post("http://localhost:5000/api/elections", newElection)
            .then((response) => setElections([...elections, response.data]))
            .catch((error) => console.error("Error adding election:", error));
    };

    const handleAddComment = () => {
        if (newComment.trim() !== "") {
            setComments([...comments, { id: comments.length + 1, text: newComment }]);
            setNewComment(""); // Reset input field
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case "Active":
                return "#4CAF50"; // Green for active
            case "Closed":
                return "#F44336"; // Red for closed
            case "Upcoming":
                return "#2196F3"; // Blue for upcoming
            default:
                return "#9E9E9E"; // Gray for unknown
        }
    };

    return (
        <div className="dashboard-container">
            <h2>Dashboard</h2>
            <p>Welcome to the Decentralized Voting System Dashboard!</p>

            {/* Election List */}
            <div className="election-section">
                <h3>Available Elections</h3>
                <div className="election-cards">
                    {elections.map((election) => (
                        <div
                            key={election.id}
                            className="election-card"
                            style={{ borderColor: getStatusColor(election.status) }}
                        >
                            <h4>{election.name}</h4>
                            <p className="status" style={{ color: getStatusColor(election.status) }}>
                                {election.status}
                            </p>
                            <FaRegCalendarAlt className="icon" />
                        </div>
                    ))}
                </div>
                <button className="auth-button" onClick={addElection}>
                    Add Election
                </button>
            </div>

            {/* News Section */}
            <div className="news-section">
                <h3>Latest Election News</h3>
                <div className="news-cards">
                    {news.length > 0 ? (
                        news.map((item) => (
                            <div className="news-card" key={item.id}>
                                <img src={item.imageUrl} alt="news" className="news-image" />
                                <div className="news-content">
                                    <h4>{item.title}</h4>
                                    <p>{item.description}</p>
                                    <FaRegFlag className="icon" />
                                </div>
                            </div>
                        ))
                    ) : (
                        <p>Loading news...</p>
                    )}
                </div>
            </div>

            {/* Voter Opinions Section */}
            <div className="comments-section">
                <h3>Voter Opinions</h3>
                <div className="comments-display">
                    {comments.map((comment) => (
                        <div className="comment-card" key={comment.id}>
                            <p>{comment.text}</p>
                            <FaRegCommentDots className="icon" />
                        </div>
                    ))}
                </div>
                <div className="add-comment">
                    <input
                        type="text"
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="Share your opinion..."
                        className="comment-input"
                    />
                    <button onClick={handleAddComment} className="auth-button">
                        Add Opinion
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
