import React, { useState, useEffect } from "react";
import { FaRegCalendarAlt, FaRegFlag, FaRegCommentDots } from "react-icons/fa";
import "./Dashboard.css";

const Dashboard = () => {
    const [elections, setElections] = useState([
        { id: 1, name: "Student Council Election", status: "Active" },
        { id: 2, name: "Company Representative Election", status: "Closed" },
    ]);

    const [news, setNews] = useState([]);
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState("");

    useEffect(() => {
        // Fetch election news dynamically
        fetch("https://newsapi.org/v2/everything?q=elections&apiKey=8280846cdd1a49ef880809767d0e61c8") // Replace with your API key
            .then((response) => response.json())
            .then((data) => {
                const articles = data.articles.map((article, index) => ({
                    id: index + 1,
                    title: article.title,
                    description: article.description,
                    imageUrl: article.urlToImage || "https://via.placeholder.com/150",
                }));
                setNews(articles);
            })
            .catch((error) => console.error("Error fetching news:", error));
    }, []);

    const addElection = () => {
        setElections([
            ...elections,
            { id: elections.length + 1, name: "New Election", status: "Upcoming" },
        ]);
    };

    const handleAddComment = () => {
        if (newComment.trim() !== "") {
            setComments([...comments, { id: comments.length + 1, text: newComment }]);
            setNewComment(""); // Reset input field
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case "Active":
                return "#4CAF50"; // Green for active
            case "Closed":
                return "#F44336"; // Red for closed
            case "Upcoming":
                return "#2196F3"; // Blue for upcoming
            default:
                return "#9E9E9E"; // Gray for unknown
        }
    };

    return (
        <div className="dashboard-container">
            <h2>Dashboard</h2>
            <p><h1>Welcome to the Decentralized Voting System Dashboard!</h1></p>

            {/* Election List */}
            <div className="election-section">
                <h3>Available Elections</h3>
                <div className="election-cards">
                    {elections.map((election) => (
                        <div
                            key={election.id}
                            className="election-card"
                            style={{ borderColor: getStatusColor(election.status) }}
                        >
                            <h4>{election.name}</h4>
                            <p className="status" style={{ color: getStatusColor(election.status) }}>
                                {election.status}
                            </p>
                            <FaRegCalendarAlt className="icon" />
                        </div>
                    ))}
                </div>
                <button className="auth-button" onClick={addElection}>
                    Add Election
                </button>
            </div>



            {/* Voter Opinions Section */}
            <div className="comments-section">
                <h3>Voter Opinions</h3>
                <div className="comments-display">
                    {comments.map((comment) => (
                        <div className="comment-card" key={comment.id}>
                            <p>{comment.text}</p>
                            <FaRegCommentDots className="icon" />
                        </div>
                    ))}
                </div>
                <div className="add-comment">
                    <input
                        type="text"
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="Share your opinion..."
                        className="comment-input"
                    />
                    <button onClick={handleAddComment} className="auth-button">
                        Add Opinion
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
