import React, { useState } from "react";
import "./Dashboard.css";

const Dashboard = () => {
    const [elections, setElections] = useState([
        { id: 1, name: "Student Council Election", status: "Active" },
        { id: 2, name: "Company Representative Election", status: "Closed" },
    ]);
    const [news, setNews] = useState([
        {
            id: 1,
            title: "New Election Guidelines Released",
            description: "The new guidelines for student elections have been released. Please review them carefully.",
            imageUrl: "https://via.placeholder.com/150",
        },
        {
            id: 2,
            title: "Election Results for Student Council",
            description: "The results for the Student Council election have been announced.",
            imageUrl: "https://via.placeholder.com/150",
        },
    ]);

    const addElection = () => {
        setElections([
            ...elections,
            { id: elections.length + 1, name: "New Election", status: "Upcoming" },
        ]);
    };

    const getStatusColor = (status) => {
        switch (status) {
            case "Active":
                return "green";
            case "Closed":
                return "red";
            case "Upcoming":
                return "blue";
            default:
                return "gray";
        }
    };

    return (
        <div className="dashboard-container">
            <h2>Dashboard</h2>
            <p>Welcome to the Decentralized Voting System Dashboard!</p>

            {/* Election List */}
            <div className="election-section">
                <h3>Available Elections:</h3>
                <ul>
                    {elections.map((election) => (
                        <li
                            key={election.id}
                            style={{
                                backgroundColor: getStatusColor(election.status),
                                color: "white",
                                padding: "10px",
                                borderRadius: "5px",
                                marginBottom: "10px",
                            }}
                        >
                            <strong>{election.name}</strong> - {election.status}
                        </li>
                    ))}
                </ul>
                <button className="auth-button" onClick={addElection}>
                    Add Election
                </button>
            </div>

            {/* News Section */}
            <div className="news-section">
                <h3>Latest News:</h3>
                <div className="news-cards">
                    {news.map((item) => (
                        <div className="news-card" key={item.id}>
                            <img src={item.imageUrl} alt="news" className="news-image" />
                            <div className="news-content">
                                <h4>{item.title}</h4>
                                <p>{item.description}</p>
                            </div>
                        </div>
                    ))}
                </div>
                <button
                    className="auth-button"
                    onClick={() => alert("Feature to add news coming soon!")}
                >
                    Add News
                </button>
            </div>
        </div>
    );
};

export default Dashboard;
