import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Webcam from "react-webcam";
import * as faceapi from 'face-api.js';

const Login = () => {
    const navigate = useNavigate();
    const webcamRef = useRef(null);

    const [formData, setFormData] = useState({
        email: "",
        password: "",
        aadharNumber: "",
    });

    const [authStep, setAuthStep] = useState('credentials');
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState("");
    const [faceDetected, setFaceDetected] = useState(false);
    const [faceMatched, setFaceMatched] = useState(false);

    // Email validation
    const validateEmail = (email) => {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    };

    // Aadhar validation
    const validateAadhar = (aadhar) => {
        return /^\d{12}$/.test(aadhar);
    };

    const handleCredentialsSubmit = async () => {
        if (!validateEmail(formData.email)) {
            setError("Please enter a valid email address");
            return;
        }
        if (!formData.password) {
            setError("Please enter your password");
            return;
        }

        setIsLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep('aadhar');
            setError("");
        } catch (error) {
            setError("Invalid credentials");
        } finally {
            setIsLoading(false);
        }
    };

    const handleAadharSubmit = async () => {
        if (!validateAadhar(formData.aadharNumber)) {
            setError("Please enter a valid 12-digit Aadhar number");
            return;
        }

        setIsLoading(true);
        try {
            await new Promise(resolve => setTimeout(resolve, 1000));
            setAuthStep('face');
            setError("");
            await loadFaceDetectionModels();
        } catch (error) {
            setError("Aadhar verification failed");
        } finally {
            setIsLoading(false);
        }
    };

    const loadFaceDetectionModels = async () => {
        try {
            await Promise.all([
                faceapi.nets.tinyFaceDetector.loadFromUri('/models'),
                faceapi.nets.faceLandmark68Net.loadFromUri('/models'),
                faceapi.nets.faceRecognitionNet.loadFromUri('/models')
            ]);
        } catch (error) {
            setError("Failed to load face detection. Please refresh and try again.");
        }
    };

    const handleFaceDetection = async () => {
        if (!webcamRef.current) return;

        const interval = setInterval(async () => {
            try {
                const video = webcamRef.current.video;
                if (video) {
                    const detection = await faceapi
                        .detectSingleFace(video, new faceapi.TinyFaceDetectorOptions())
                        .withFaceLandmarks();

                    if (detection) {
                        setFaceDetected(true);
                        clearInterval(interval);
                        setTimeout(() => {
                            setFaceMatched(true);
                            setTimeout(() => navigate("/dashboard"), 1000);
                        }, 1500);
                    }
                }
            } catch (error) {
                console.error("Face detection error:", error);
            }
        }, 100);

        return () => clearInterval(interval);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        // Special handling for Aadhar input - only allow numbers
        if (name === 'aadharNumber') {
            const numericValue = value.replace(/\D/g, '');
            setFormData(prev => ({
                ...prev,
                [name]: numericValue
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
        setError("");
    };

    return (
        <div className="auth-container">
            <div className="auth-card">
                <h2>Secure Login</h2>
                {error && <div className="error-message">{error}</div>}

                {authStep === 'credentials' && (
                    <form onSubmit={(e) => { e.preventDefault(); handleCredentialsSubmit(); }}>
                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                placeholder="Enter your email"
                                value={formData.email}
                                onChange={handleInputChange}
                                disabled={isLoading}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input
                                type="password"
                                id="password"
                                name="password"
                                placeholder="Enter your password"
                                value={formData.password}
                                onChange={handleInputChange}
                                disabled={isLoading}
                            />
                        </div>
                        <button
                            className="auth-button"
                            type="submit"
                            disabled={isLoading}
                        >
                            {isLoading ? "Verifying..." : "Next"}
                        </button>
                        <button
                            className="link-button"
                            onClick={() => navigate("/signup")}
                            type="button"
                            disabled={isLoading}
                        >
                            Don't have an account? Sign Up
                        </button>
                    </form>
                )}

                {authStep === 'aadhar' && (
                    <div className="form-group">
                        <label htmlFor="aadhar">Aadhar Number</label>
                        <input
                            type="text"
                            id="aadhar"
                            name="aadharNumber"
                            value={formData.aadharNumber}
                            onChange={handleInputChange}
                            placeholder="Enter 12-digit Aadhar number"
                            maxLength={12}
                            disabled={isLoading}
                        />
                        <button
                            className="auth-button"
                            onClick={handleAadharSubmit}
                            disabled={isLoading || formData.aadharNumber.length !== 12}
                        >
                            {isLoading ? "Verifying..." : "Verify Aadhar"}
                        </button>
                    </div>
                )}

                {authStep === 'face' && (
                    <div className="face-auth-container">
                        <h3>Face Verification</h3>
                        <div className="webcam-container">
                            <Webcam
                                ref={webcamRef}
                                screenshotFormat="image/jpeg"
                                className="webcam"
                                onUserMedia={handleFaceDetection}
                            />
                            {faceDetected && (
                                <div className={`face-status ${faceMatched ? 'matched' : ''}`}>
                                    {faceMatched ? 'Face Verified ✓' : 'Detecting Face...'}
                                </div>
                            )}
                        </div>
                        {faceMatched && (
                            <div className="success-message">
                                Authentication successful! Redirecting...
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
