import React, { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { FaRegCalendarAlt, FaRegFlag, FaRegCommentDots, FaUserCircle, FaChartBar, FaEdit, FaTrash, FaVoteYea } from "react-icons/fa";
import "./Dashboard.css";

const STATUS_COLORS = {
    Active: "#4CAF50",
    Closed: "#F44336",
    Upcoming: "#2196F3",
    Default: "#9E9E9E"
};

const NEWS_API_KEY = "8280846cdd1a49ef880809767d0e61c8";

const Dashboard = () => {
    const navigate = useNavigate();
    const chatEndRef = useRef(null);

    // States
    const [elections, setElections] = useState([
        {
            id: 1,
            name: "Student Council Election",
            status: "Active",
            description: "Vote for your student representatives",
            endDate: "2024-12-20",
            candidates: [
                { id: 1, name: "John Doe", position: "President", votes: 145 },
                { id: 2, name: "Jane Smith", position: "Vice President", votes: 120 },
            ]
        },
        // ... existing elections
    ]);

    const [selectedElection, setSelectedElection] = useState(null);
    const [editingElection, setEditingElection] = useState(null);
    const [showAddModal, setShowAddModal] = useState(false);
    const [newElectionData, setNewElectionData] = useState({
        name: "",
        description: "",
        status: "Upcoming",
        endDate: ""
    });
    const [newCandidate, setNewCandidate] = useState({ name: "", position: "" });
    const [username, setUsername] = useState("Anonymous");
    const [statusFilter, setStatusFilter] = useState("All");

    // Add other existing states...

    // New functionality handlers
    const handleDeleteElection = (electionId) => {
        if (window.confirm("Are you sure you want to delete this election?")) {
            setElections(prev => prev.filter(e => e.id !== electionId));
        }
    };

    const handleEditElection = (election) => {
        setEditingElection(election);
    };

    const handleSaveElection = (updatedElection) => {
        setElections(prev =>
            prev.map(e => e.id === updatedElection.id ? updatedElection : e)
        );
        setEditingElection(null);
    };

    const handleAddCandidate = () => {
        if (!newCandidate.name || !newCandidate.position) return;

        const candidateToAdd = {
            id: selectedElection.candidates.length + 1,
            name: newCandidate.name,
            position: newCandidate.position,
            votes: 0
        };

        const updatedElections = elections.map(election =>
            election.id === selectedElection.id
                ? { ...election, candidates: [...election.candidates, candidateToAdd] }
                : election
        );

        setElections(updatedElections);
        setSelectedElection(prev => ({
            ...prev,
            candidates: [...prev.candidates, candidateToAdd]
        }));
        setNewCandidate({ name: "", position: "" });
    };

    const handleVote = (candidateId) => {
        const hasVoted = localStorage.getItem(`voted_${selectedElection.id}`);
        if (hasVoted) {
            alert("You have already voted in this election!");
            return;
        }

        const updatedElections = elections.map(election => {
            if (election.id === selectedElection.id) {
                return {
                    ...election,
                    candidates: election.candidates.map(candidate =>
                        candidate.id === candidateId
                            ? { ...candidate, votes: candidate.votes + 1 }
                            : candidate
                    )
                };
            }
            return election;
        });

        setElections(updatedElections);
        localStorage.setItem(`voted_${selectedElection.id}`, "true");
        setSelectedElection(prev => ({
            ...prev,
            candidates: prev.candidates.map(candidate =>
                candidate.id === candidateId
                    ? { ...candidate, votes: candidate.votes + 1 }
                    : candidate
            )
        }));
    };

    const handleDeleteComment = (commentId) => {
        setComments(prev => prev.filter(c => c.id !== commentId));
    };

    // Modified election card with edit/delete buttons
    const ElectionCard = ({ election }) => (
        <div
            className="election-card interactive"
            onClick={() => handleElectionClick(election)}
            style={{ borderLeft: `5px solid ${getStatusColor(election.status)}` }}
        >
            <div className="election-info">
                <div className="election-header">
                    <h3>{election.name}</h3>
                    <div className="election-actions">
                        <FaEdit
                            className="icon-button"
                            onClick={(e) => {
                                e.stopPropagation();
                                handleEditElection(election);
                            }}
                        />
                        <FaTrash
                            className="icon-button"
                            onClick={(e) => {
                                e.stopPropagation();
                                handleDeleteElection(election.id);
                            }}
                        />
                    </div>
                </div>
                <p className="description">{election.description}</p>
                <p className="end-date">Ends: {election.endDate}</p>
                <p className="status" style={{ color: getStatusColor(election.status) }}>
                    {election.status}
                </p>
            </div>
            <FaRegCalendarAlt
                className="icon"
                color={getStatusColor(election.status)}
                size={24}
            />
        </div>
    );

    // Filtered elections
    const filteredElections = statusFilter === "All"
        ? elections
        : elections.filter(e => e.status === statusFilter);

    return (
        <div className="dashboard-container">
            {/* Add username input */}
            <div className="profile-section">
                <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Enter your username"
                    className="username-input"
                />
            </div>

            {/* Election Section with Filters */}
            <section className="election-section">
                <div className="section-header">
                    <h2>Available Elections</h2>
                    <div className="filters">
                        <button onClick={() => setStatusFilter("All")}>All</button>
                        {Object.keys(STATUS_COLORS).map(status => (
                            <button key={status} onClick={() => setStatusFilter(status)}>
                                {status}
                            </button>
                        ))}
                    </div>
                    <button className="auth-button" onClick={() => setShowAddModal(true)}>
                        Add Election
                    </button>
                </div>

                <div className="election-cards">
                    {filteredElections.map(election => (
                        <ElectionCard key={election.id} election={election} />
                    ))}
                </div>
            </section>

            {/* Add Election Modal */}
            {showAddModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>Create New Election</h2>
                        <input
                            type="text"
                            placeholder="Election Name"
                            value={newElectionData.name}
                            onChange={(e) => setNewElectionData({ ...newElectionData, name: e.target.value })}
                        />
                        {/* Add other fields */}
                        <button onClick={() => {
                            const newElection = {
                                id: elections.length + 1,
                                ...newElectionData,
                                candidates: []
                            };
                            setElections([...elections, newElection]);
                            setShowAddModal(false);
                        }}>
                            Create
                        </button>
                        <button onClick={() => setShowAddModal(false)}>Cancel</button>
                    </div>
                </div>
            )}

            {/* Enhanced Candidates Modal */}
            {showCandidates && selectedElection && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>{selectedElection.name} - Candidates</h2>

                        {/* Add Candidate Form */}
                        <div className="add-candidate-form">
                            <input
                                type="text"
                                placeholder="Candidate Name"
                                value={newCandidate.name}
                                onChange={(e) => setNewCandidate({ ...newCandidate, name: e.target.value })}
                            />
                            <input
                                type="text"
                                placeholder="Position"
                                value={newCandidate.position}
                                onChange={(e) => setNewCandidate({ ...newCandidate, position: e.target.value })}
                            />
                            <button onClick={handleAddCandidate}>Add Candidate</button>
                        </div>

                        {/* Candidates Grid with Voting */}
                        <div className="candidates-grid">
                            {selectedElection.candidates.map(candidate => (
                                <div key={candidate.id} className="candidate-card">
                                    <FaUserCircle size={50} className="candidate-avatar" />
                                    <h3>{candidate.name}</h3>
                                    <p>{candidate.position}</p>
                                    {selectedElection.status === "Active" && (
                                        <button
                                            className="vote-button"
                                            onClick={() => handleVote(candidate.id)}
                                        >
                                            <FaVoteYea /> Vote
                                        </button>
                                    )}
                                    {selectedElection.status === "Closed" && (
                                        <div className="votes-info">
                                            <FaChartBar /> {candidate.votes} votes
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                        <button className="auth-button" onClick={() => setShowCandidates(false)}>
                            Close
                        </button>
                    </div>
                </div>
            )}

            {/* Enhanced Comments with Delete */}
            {comments.map(comment => (
                <div className="comment-card" key={comment.id}>
                    {/* ... existing comment content */}
                    {comment.user === username && (
                        <button
                            className="delete-comment"
                            onClick={() => handleDeleteComment(comment.id)}
                        >
                            Delete
                        </button>
                    )}
                </div>
            ))}

            {/* Rest of the components... */}
        </div>
    );
};

export default Dashboard;
